<?phpnamespace backend\models;use yii\base\Model;use yii\web\UploadedFile;/** * UploadForm is the model behind the upload form. */class UploadForm extends Model{    /**     * @var UploadedFile file attribute     */    public $file;    public $file2;    public $file3;    public $quotation;    public $file4;    /**     * @return array the validation rules.     */    public function rules()    {        return [            [['file','file2','file3'], 'file',                'skipOnEmpty' => true,                'checkExtensionByMimeType' => false,                'extensions' => ['xlsx','xls'],                'wrongExtension'=>'只能上传{extensions}类型文件！'            ],            [['quotation'], 'file',                'skipOnEmpty' => true,                'checkExtensionByMimeType' => false,                'extensions' => ['pdf','jpg','png','jpeg'],                'wrongExtension'=>'只能上传{extensions}类型文件！',                'maxFiles' => 5,            ],            [['file4'], 'file',                'skipOnEmpty' => true,                'checkExtensionByMimeType' => false,                'extensions' => ['pdf','jpg','png','jpeg','doc','docx'],                'wrongExtension'=>'只能上传{extensions}类型文件！',                'maxFiles' => 5,            ],        ];    }}?>