<?phpnamespace backend\models;use Yii;/** * This is the model class for table "{{%user_address}}". * * @property integer $id * @property integer $provinces * @property integer $city * @property integer $area * @property string $content * @property string $user * @property string $phone */class UserAddress extends \yii\db\ActiveRecord{    /**     * @inheritdoc     */    public static function tableName()    {        return '{{%address}}';    }    /**     * @inheritdoc     */    public function rules()    {        return [            [['provinces', 'city', 'area', 'user', 'phone','content','user_id'], 'required'],            [['content'], 'string'],            [['user'], 'string', 'max' => 50],            [['phone'], 'string', 'max' => 20],            [['is_default','is_default2'],'safe'],            ['phone','match','pattern'=>'/^[1][3578][0-9]{9}$/','message'=>'不是一个有效的手机号码'],        ];    }    /**     * @inheritdoc     */    public function attributeLabels()    {        return [            'id' => 'ID',            'provinces' => '省',            'city' => '市',            'area' => '区',            'content' => '详细地址',            'user' => '收货人',            'phone' => '电话',        ];    }}