<?phpnamespace backend\search;use Yii;use yii\base\Model;use yii\data\ActiveDataProvider;use backend\models\Order;/** * OrderSearch represents the model behind the search form about `backend\models\Order`. */class OrderSearch extends Order{    public $time1;    public $time2;    /**     * @inheritdoc     */    public function rules()    {        return [            [['id', 'type', 'user_id', 'address_id', 'invoice_id', 'pay_method', 'pay_status', 'status', 'created_at', 'updated_at', 'paid_time', 'delivery_time', 'confirm_time', 'is_delete', 'category'], 'integer'],            [['order_number', 'consignee', 'phone', 'address_detail', 'express', 'content','time1','time2'], 'safe'],            [['total_price', 'freight','is_delivery'], 'number'],        ];    }    /**     * @inheritdoc     */    public function scenarios()    {        // bypass scenarios() implementation in the parent class        return Model::scenarios();    }    /**     * Creates data provider instance with search query applied     *     * @param array $params     *     * @return ActiveDataProvider     */    public function search($params)    {        $query = Order::find();        // add conditions that should always apply here        $dataProvider = new ActiveDataProvider([            'query' => $query,            'sort' => [                'defaultOrder' => [                    'created_at' => SORT_DESC,                ]            ],        ]);        $this->load($params);        if (!$this->validate()) {            // uncomment the following line if you do not want to return any records when validation fails            // $query->where('0=1');            return $dataProvider;        }        // grid filtering conditions        $query->andFilterWhere([            'id' => $this->id,            'type' => $this->type,            'user_id' => $this->user_id,            'address_id' => $this->address_id,            'invoice_id' => $this->invoice_id,            'total_price' => $this->total_price,            'pay_method' => $this->pay_method,            'pay_status' => $this->pay_status,            'status' => $this->status,            'paid_time' => $this->paid_time,            'delivery_time' => $this->delivery_time,            'confirm_time' => $this->confirm_time,            'is_delete' => $this->is_delete,            'category' => $this->category,            'freight' => $this->freight,            'is_delivery'=>$this->is_delivery,        ]);        $query->andFilterWhere(['like', 'order_number', $this->order_number])            ->andFilterWhere(['like', 'consignee', $this->consignee])            ->andFilterWhere(['like', 'phone', $this->phone])            ->andFilterWhere(['like', 'address_detail', $this->address_detail])            ->andFilterWhere(['like', 'express', $this->express])            ->andFilterWhere(['like', 'content', $this->content]);        if($this->time1 and $this->time2){            $begin=strtotime($this->time1);            $end=strtotime($this->time2)+24*3600-1;            $query->andWhere(['between','created_at',$begin,$end]);        }        return $dataProvider;    }}