<?phpnamespace frontend\controllers;use backend\models\Comments;use backend\models\DataSharing;use backend\models\Faqs;use backend\models\Home;use backend\models\News;use backend\models\NewsCategory;use backend\models\Position;use backend\models\PositionApply;use backend\models\SetImage;use backend\models\UploadForm;use backend\models\Video;use backend\search\NewsSearch;use backend\models\ProvinceUser;use common\components\Helper;use Yii;use yii\data\Pagination;use yii\web\UploadedFile;/** * Index controller */class AboutController extends FController{    public function actionIndex(){        $data['banner1']=SetImage::getOne(['type'=>3]);        $data['banner2']=SetImage::getOne(['type'=>8]);        $data['banner3']=SetImage::getList(['type'=>4]);        $data['banner4']=SetImage::getOne(['type'=>5]);        $data['banner5']=SetImage::getList(['type'=>6]);        $data['banner6']=SetImage::getOne(['type'=>7]);        return $this->render('index',['data'=>$data]);    }    public function actionGsjj(){        $data['banner1']=SetImage::getOne(['type'=>3]);        $data['banner2']=SetImage::getOne(['type'=>8]);        return $this->render('gsjj',['data'=>$data]);    }    public function actionGsys(){        $data['banner1']=SetImage::getOne(['type'=>3]);        $data['banner2']=SetImage::getOne(['type'=>9]);        return $this->render('gsys',['data'=>$data]);    }    public function actionJyln(){        $data['banner1']=SetImage::getOne(['type'=>3]);        $data['banner2']=SetImage::getOne(['type'=>5]);        return $this->render('jyln',['data'=>$data]);    }    public function actionFzlc(){        $data['banner1']=SetImage::getOne(['type'=>3]);        $data['banner2']=SetImage::getList(['type'=>4]);        return $this->render('fzlc',['data'=>$data]);    }    public function actionZzry(){        $data['banner1']=SetImage::getOne(['type'=>3]);        $data['banner2']=SetImage::getList(['type'=>10]);        return $this->render('zzry',['data'=>$data]);    }    public function actionHzzs(){        $data['banner1']=SetImage::getOne(['type'=>3]);        $data['banner2']=SetImage::getList(['type'=>6]);        return $this->render('hzzs',['data'=>$data]);    }    public function actionNews(){        $data['banner1']=SetImage::getOne(['type'=>3]);        $new_data=News::find()->where(['language'=>Yii::$app->language]);        $pages = new Pagination(['totalCount' => $new_data->count(), 'pageSize' => 6]);        $news = $new_data->offset($pages->offset)->orderBy('sort asc,created_at desc,id desc')->limit($pages->limit)->all();        $data['news']=$news;        return $this->render('news',['data'=>$data,'pages'=>$pages]);    }    public function actionDetail(){        $model=News::findOne(Yii::$app->request->get('id'));        $data['banner1']=SetImage::getOne(['type'=>3]);        $after=News::find()->where(['type'=>$model->type])->andWhere(['language'=>Yii::$app->language])->andWhere(['<','created_at',$model->created_at])->orderBy('created_at desc')->limit(1)->one();        $before=News::find()->where(['type'=>$model->type])->andWhere(['language'=>Yii::$app->language])->andWhere(['>','created_at',$model->created_at])->orderBy('created_at asc')->limit(1)->one();        return $this->render('detail',['model'=>$model,'before'=>$before,'after'=>$after,'data'=>$data]);    }}