<?phpnamespace frontend\controllers;use backend\models\News;use backend\models\SetCategory;use backend\models\SetImage;use Yii;use yii\data\Pagination;/** * Index controller */class CompanyController extends FController{    public function actionIndex(){        $banner=SetImage::getOne(['type'=>39]);        $category=SetCategory::getList(['type'=>40]);        $category_id=Yii::$app->request->get('category_id');        if(!$category_id){            $category_now=SetCategory::getOne(['type'=>40]);            if(!$category_now){                $list=[];            }else{                $list=SetImage::getList(['category_id'=>$category_now->id]);            }            $category_id=$category_now->id;        }else{            $list=SetImage::getList(['category_id'=>$category_id]);        }        return $this->render('index',['banner'=>$banner,'category'=>$category,'list'=>$list,'category_id'=>$category_id]);    }    public function actionDevelopment(){        $banner=SetImage::getOne(['type'=>41]);        $banner2=SetImage::getOne(['type'=>42]);        return $this->render('development',['banner'=>$banner,'banner2'=>$banner2]);    }}