<?php/** * */namespace frontend\controllers;use Yii;use common\controllers\BaseController;use yii\web\NotFoundHttpException;class FController extends BaseController{    public          $_pageSize = 10;//分页大小    public          $enableCsrfValidation = false;//csrf验证        /**     * 自动运行     */    /**     * @param $errors     * @return string     * 解析Yii2错误信息     */    public function analysisError($errors)    {        foreach ($errors as $error)        {            foreach ($error as $vo)            {                return $vo;            }        }        return "操作失败";    }    /**     * @param $msgText  -错误内容     * @param $skipUrl  -跳转链接     * @param $msgType  -提示类型     * @param int $closeTime -提示关闭时间     * @return mixed     * 错误提示信息     */    public function message($msgText,$skipUrl,$msgType="",$closeTime=5)    {        $closeTime = (int)$closeTime;        //如果是成功的提示则默认为3秒关闭时间        if(!$closeTime && $msgType == "success" || !$msgType)        {            $closeTime = 3;        }        $html = $msgText;        switch ($msgType)        {            case "success" :                Yii::$app->getSession()->setFlash('success',$html);                break;            case "error" :                Yii::$app->getSession()->setFlash('error',$html);                break;            case "info" :                Yii::$app->getSession()->setFlash('info',$html);                break;            case "warning" :                Yii::$app->getSession()->setFlash('warning',$html);                break;            default :                Yii::$app->getSession()->setFlash('success',$html);                break;        }        return $skipUrl;    }    public function hintText($msg,$closeTime)    {        $text = $msg." <span class='closeTimeYl'>".$closeTime."</span>秒后自动关闭...";        return $text;    }}