<?phpnamespace frontend\controllers;use backend\models\Address;use backend\models\Goods;use backend\models\Order;use backend\models\User;use backend\models\UserLevel;use Yii;use yii\helpers\Url;/** * Index controller */class GoodsController extends FController{    public function actionDetail(){        $id=Yii::$app->request->get('id');        $goods=Goods::findOne($id);        $number=0;        if(Yii::$app->session->get('user_id')){            $user=User::findOne(Yii::$app->session->get('user_id'));            $order=Order::find()->where(['user_id'=>Yii::$app->session->get('user_id')])->andWhere(['>=','status',2])->limit(1)->one();            if($order){                if($user['level_id']>0){                    $level=UserLevel::findOne($user['level_id']);                    if($level->number>0){                        $number=$level->number;                    }                }            }        }        return $this->render('detail',['goods'=>$goods,'number'=>$number]);    }    public function actionDetail2(){        $id=Yii::$app->request->get('id');        $goods=Goods::findOne($id);        return $this->render('detail2',['goods'=>$goods]);    }    public function actionDetail3(){        $id=Yii::$app->request->get('id');        $goods=Goods::findOne($id);        return $this->render('detail3',['goods'=>$goods]);    }}