<?phpnamespace frontend\controllers;use backend\models\News;use backend\models\NewsCategory;use backend\models\SetImage;use Yii;use yii\data\Pagination;/** * Index controller */class ServiceController extends FController{    public function actionIndex(){        $banner=SetImage::getOne(['type'=>18]);        $data=SetImage::find()->where(['type'=>19]);        $pages = new Pagination(['totalCount' => $data->count(), 'pageSize' => 6]);        $model = $data->offset($pages->offset)->orderBy('sort asc,id desc')->limit($pages->limit)->all();        return $this->render('index',['banner'=>$banner,'model'=>$model,'pages'=>$pages]);    }    public function actionDetail(){        $model=SetImage::findOne(Yii::$app->request->get('id'));        $banner=SetImage::getOne(['type'=>17]);        $before=SetImage::find()->where(['type'=>$model['type']])->andWhere(['>','id',$model->id])->orderBy('sort desc,id asc')->limit(1)->one();        $after=SetImage::find()->where(['type'=>$model['type']])->andWhere(['<','id',$model->id])->orderBy('sort asc,id desc')->limit(1)->one();        return $this->render('detail',['banner'=>$banner,'model'=>$model,'before'=>$before,'after'=>$after]);    }}