<?phpnamespace frontend\controllers;use backend\models\News;use backend\models\Position;use backend\models\SetCategory;use backend\models\SetImage;use backend\models\UserApply;use Yii;use yii\base\Object;use yii\data\Pagination;/** * Index controller */class SupportController extends FController{    public function actionPtcj(){        $data['banner1']=SetImage::getOne(['type'=>24]);        $data['banner2']=SetImage::getOne(['type'=>20]);        return $this->render('ptcj',['data'=>$data]);    }    public function actionSjfw(){        $data['banner1']=SetImage::getOne(['type'=>24]);        $data['banner2']=SetImage::getOne(['type'=>21]);        return $this->render('sjfw',['data'=>$data]);    }    public function actionMjdz(){        $data['banner1']=SetImage::getOne(['type'=>24]);        $data['banner2']=SetImage::getOne(['type'=>22]);        return $this->render('mjdz',['data'=>$data]);    }    public function actionFzdy(){        $data['banner1']=SetImage::getOne(['type'=>24]);        $data['banner2']=SetImage::getOne(['type'=>23]);        return $this->render('fzdy',['data'=>$data]);    }    public function actionJcfw(){        $data['banner1']=SetImage::getOne(['type'=>24]);        $data['banner2']=SetImage::getOne(['type'=>36]);        return $this->render('jcfw',['data'=>$data]);    }    public function actionXtfa(){        $data['banner1']=SetImage::getOne(['type'=>24]);        $new_data=SetImage::find()->where(['language'=>Yii::$app->language,'type'=>25]);        $pages = new Pagination(['totalCount' => $new_data->count(), 'pageSize' => 6]);        $news = $new_data->offset($pages->offset)->orderBy('sort asc,id desc')->limit($pages->limit)->all();        $data['banner2']=$news;        return $this->render('xtfa',['data'=>$data,'pages'=>$pages]);    }    public function actionCylm(){        $data['banner1']=SetImage::getOne(['type'=>24]);        $data['banner2']=SetImage::getOne(['type'=>26]);        return $this->render('cylm',['data'=>$data]);    }    public function actionZspt(){        $data['banner1']=SetImage::getOne(['type'=>24]);        $data['banner2']=SetImage::getOne(['type'=>27]);        return $this->render('zspt',['data'=>$data]);    }    public function actionXzzx(){        $data['banner1']=SetImage::getOne(['type'=>24]);        $new_data=SetImage::find()->where(['language'=>Yii::$app->language,'type'=>28]);        $pages = new Pagination(['totalCount' => $new_data->count(), 'pageSize' => 6]);        $news = $new_data->all();        foreach ($news as $k=>$v){            $data['banner2'][$v->subtitle][]=$v;        }        return $this->render('xzzx',['data'=>$data,'pages'=>$pages]);    }}