<?phpnamespace app\commands;use yii\console\Controller;class SwTcpController extends Controller {    private $_server;    public function actionStart() {        $this->_server = new \swoole_server("0.0.0.0", 8081);        $this->_server->set([            'worker_num' => 4,            'daemonize' => false        ]);        $this->_server->on('connect', [$this, 'onConnect']);        $this->_server->on('receive', [$this, 'onReceive']);        $this->_server->on('close', [$this, 'onClose']);        echo "TCP server running at tcp://0.0.0.0:8081\n";        $this->_server->start();    }    public function onConnect($server, $fd) {        echo "Client {$fd} connected\n";    }    public function onReceive($server, $fd, $reactorId, $data) {        $server->send($fd, "Server received: ".$data);    }    public function onClose($server, $fd) {        echo "Client {$fd} closed connection\n";    }}?>