<?phpnamespace frontend\models;use common\models\LoginForm;use frontend\models\FrontendUser;/** * */class UserLoginForm extends LoginForm{    /**     * @inheritdoc     */    public function rules()    {        return [            // username and password are both required            [['username', 'password'], 'required'],            // rememberMe must be a boolean value            ['rememberMe', 'boolean'],            // password is validated by validatePassword()            ['password', 'validatePassword'],        ];    }    public function attributeLabels()    {        return [            'username' => '用户名',            'password' => '密码',        ];    }    /**     * Finds user by [[username]]     *     * @return User|null     */    protected function getUser()    {        if ($this->_user === null) {            $user=ProvinceUser::find()->where(['or',['username'=>$this->username],['email'=>$this->username],['mobile_phone'=>$this->username]])->one();            $this->_user = $user;        }        return $this->_user;    }}