<?phpnamespace frontend\models;use common\models\LoginForm2;use frontend\models\FrontendUser;/** * */class UserLoginForm2 extends LoginForm2{    /**     * @inheritdoc     */    public function rules()    {        return [            // username and password are both required            [['username', 'code'], 'required'],            // rememberMe must be a boolean value            ['rememberMe', 'boolean'],            // password is validated by validatePassword()            ['code', 'validatePassword'],        ];    }    public function attributeLabels()    {        return [            'username' => '手机号码',            'code' => '验证码',        ];    }    /**     * Finds user by [[username]]     *     * @return User|null     */    protected function getUser()    {        if ($this->_user === null) {            $user=ProvinceUser::find()->where(['username'=>$this->username])->one();            $this->_user = $user;        }        return $this->_user;    }}